import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Ratings = ({ recipeId }) => {
  const [rating, setRating] = useState(0);
  const [ratings, setRatings] = useState([]);

  const fetchRatings = () => {
    axios.get(`http://localhost:3001/ratings?recipeId=${recipeId}`)
      .then(response => {
        setRatings(response.data);
        const avgRating = response.data.reduce((acc, rating) => acc + rating.score, 0) / response.data.length;
        setRating(avgRating);
      })
      .catch(error => {
        console.error('Error fetching ratings:', error);
      });
  };

  const handleAddRating = (newRating) => {
    axios.post(`http://localhost:3001/ratings`, { recipeId, score: newRating })
      .then(response => {
        setRatings([...ratings, response.data]);
        const avgRating = [...ratings, response.data].reduce((acc, rating) => acc + rating.score, 0) / (ratings.length + 1);
        setRating(avgRating);
      })
      .catch(error => {
        console.error('Error adding rating:', error);
      });
  };

  useEffect(() => {
    fetchRatings();
  }, [recipeId]);

  return (
    <div>
      <h3>Ratings</h3>
      <div>Average Rating: {rating}</div>
      <div>
        {[1, 2, 3, 4, 5].map(star => (
          <span key={star} onClick={() => handleAddRating(star)} style={{ cursor: 'pointer' }}>
            {star <= rating ? '★' : '☆'}
          </span>
        ))}
      </div>
    </div>
  );
};

export default Ratings;
