import React from 'react';
import { useParams } from 'react-router-dom';
import Comments from './Comments';
import Rating from './Rating';

const recipes = [
  { id: 1, title: 'Butter Chicken', ingredients: ['Chicken', 'Butter', 'Tomatoes', 'Cream', 'Spices'], instructions: ['Marinate chicken', 'Cook tomatoes and spices', 'Add chicken', 'Stir in cream', 'Simmer until cooked'] },
  { id: 2, title: 'Paneer Tikka', ingredients: ['Paneer', 'Yogurt', 'Spices', 'Lemon juice', 'Capsicum', 'Onions'], instructions: ['Marinate paneer', 'Skewer with vegetables', 'Grill until cooked'] },
  { id: 3, title: 'Biryani', ingredients: ['Rice', 'Chicken', 'Yogurt', 'Spices', 'Saffron', 'Onions'], instructions: ['Marinate chicken', 'Cook rice', 'Layer rice and chicken', 'Bake until cooked'] },
  { id: 4, title: 'Chole Bhature', ingredients: ['Chickpeas', 'Tomatoes', 'Spices', 'Flour', 'Yogurt'], instructions: ['Cook chickpeas', 'Prepare bhature dough', 'Fry bhature', 'Serve with chickpeas'] },
  { id: 5, title: 'Samosa', ingredients: ['Flour', 'Potatoes', 'Peas', 'Spices', 'Oil'], instructions: ['Prepare dough', 'Make filling', 'Shape samosas', 'Deep fry until golden'] },
];

const RecipeDetails = () => {
  const { id } = useParams();
  const recipe = recipes.find((r) => r.id === parseInt(id));

  if (!recipe) {
    return <h2>Recipe not found</h2>;
  }

  return (
    <div className={`recipe-item recipe-${recipe.id}`}>
      <h2>{recipe.title}</h2>
      <p>Ingredients: {recipe.ingredients.join(', ')}</p>
      <p>Instructions: {recipe.instructions.join(' ')}</p>
      <Rating recipeId={recipe.id} />
      <Comments recipeId={recipe.id} />
    </div>
  );
};

export default RecipeDetails;
