import React, { useState, useEffect } from "react";
import axios from "axios";

const RecipeDetails = () => {
  const [recipes, setRecipes] = useState([]); // To store recipe data

  useEffect(() => {
    // Fetch recipe data from the API
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:3001/recipes"); // Update endpoint if different
        setRecipes(response.data); // Assuming the API returns an array of recipes
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  return (
    <div>
      <h1>Recipes</h1>
      <ul>
        {recipes.map((recipe) => (
          <li key={recipe.id}>
            <h3>{recipe.title}</h3>
            <p><strong>Description:</strong> {recipe.description}</p>
            <p><strong>Created At:</strong> {new Date(recipe.createdAt).toLocaleString()}</p>
            <p><strong>Updated At:</strong> {new Date(recipe.updatedAt).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RecipeDetails;
