import React, { useEffect, useState } from 'react';
import { getRecipes } from '../api/api'; // Adjust the import path as needed

const RecipeDetails = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      const fetchedRecipes = await getRecipes();
      setRecipes(fetchedRecipes);
    };

    fetchRecipes();
  }, []);

  return (
    <div>
      <h1>Recipe Details</h1>
      <ul>
        {recipes.map((recipe) => (
          <li key={recipe.id}>
            <h2>{recipe.title}</h2>
            <p>{recipe.description}</p>
            <small>Created At: {new Date(recipe.createdAt).toLocaleString()}</small>
            <small>Updated At: {new Date(recipe.updatedAt).toLocaleString()}</small>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RecipeDetails;
