import React, { useState, useEffect } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import './Navbar.css';
import Loading from './Loading';

const Navbar = () => {
  const [isLoading, setIsLoading] = useState(false);
  const location = useLocation();

  useEffect(() => {
    setIsLoading(true);
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [location]);

  return (
    <div>
      <nav>
        <ul>
          <li>
            <NavLink exact to="/" activeClassName="active">Home</NavLink>
          </li>
          <li>
            <NavLink to="/credentials" activeClassName="active">Credentials</NavLink>
          </li>
          <li>
            <NavLink to="/search" activeClassName="active">Search</NavLink>
          </li>
          <li>
            <NavLink to="/recipedetails" activeClassName="active">Recipedetails</NavLink>
          </li>
          <li>
            <NavLink to="/profile" activeClassName="active">Profile</NavLink>
          </li>
          <li>
            <NavLink to="/comments" activeClassName="active">Comments</NavLink>
          </li>
          <li>
            <NavLink to="/ratings" activeClassName="active">Ratings</NavLink>
          </li>
          <li>
            <NavLink to="/recipe-list" activeClassName="active">RecipeList</NavLink>
          </li>
        </ul>
      </nav>
      {isLoading && <Loading />}
    </div>
  );
};

export default Navbar;
