import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Comments = ({ recipeId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const sampleComments = [
    {
      recipeId: 1,
      userId: 101,
      username: "SpiceKing",
      comment: "Chicken Biryani turned out delicious with the perfect mix of spices!",
      rating: 5
    },
    {
      recipeId: 2,
      userId: 102,
      username: "VeggieQueen",
      comment: "Loved the simplicity and taste of Aloo Gobi. Perfect comfort food!",
      rating: 4
    },
    {
      recipeId: 3,
      userId: 103,
      username: "SweetTooth",
      comment: "Gulab Jamun was soft, syrupy, and delightful. Great recipe!",
      rating: 5
    },
    {
      recipeId: 4,
      userId: 104,
      username: "DalLover",
      comment: "Masoor Dal is hearty, wholesome, and so easy to make!",
      rating: 4
    },
    {
      recipeId: 5,
      userId: 105,
      username: "PaneerFan",
      comment: "Paneer Tikka Masala had just the right balance of smoky and creamy flavors!",
      rating: 5
    }
  ];

  const fetchComments = () => {
    // Simulating fetching comments from an API
    const filteredComments = sampleComments.filter(comment => comment.recipeId === recipeId);
    setComments(filteredComments);
  };

  const handleAddComment = () => {
    const newCommentData = {
      recipeId,
      userId: Date.now(), // Simulating a unique user ID
      username: "NewUser",
      comment: newComment,
      rating: 5 // Default rating for new comments
    };
    setComments([...comments, newCommentData]);
    setNewComment('');
  };

  useEffect(() => {
    fetchComments();
  }, [recipeId]);

  return (
    <div>
      <h3>Comments</h3>
      <ul>
        {comments.map((comment, index) => (
          <li key={index}>
            <strong>{comment.username}:</strong> {comment.comment} (Rating: {comment.rating})
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={newComment}
        onChange={(e) => setNewComment(e.target.value)}
        placeholder="Add a comment"
      />
      <button onClick={handleAddComment}>Submit</button>
    </div>
  );
};

export default Comments;
